css:
.bento-showcase-section { padding: 5rem 0; background-color: var(--color-light); }
.bento-showcase-section .container { width: 90%; max-width: 1000px; margin: 0 auto; }
.bento-showcase-section .section-header { text-align: center; margin-bottom: 3rem; }
.bento-showcase-section .section-header h2 { font-size: 2.5rem; font-weight: 700; color: var(--color-dark); margin-bottom: 0.5rem; }
.bento-showcase-section .section-header p { color: var(--color-secondary); }
.bento-showcase-section .bento-grid { display: grid; grid-template-columns: repeat(3, 1fr); gap: 1rem; }
.bento-showcase-section .bento-item { border-radius: 1rem; padding: 1.5rem; display: flex; flex-direction: column; justify-content: space-between; }
.bento-showcase-section .bento-item-large { grid-column: span 2; background-color: #312E48; color: var(--color-light); }
.bento-showcase-section .bento-item-header { display: flex; gap: 0.75rem; }
.bento-showcase-section .tool-icon { width: 40px; height: 40px; background: rgba(255,255,255,0.1); padding: 8px; border-radius: 50%; }
.bento-showcase-section .bento-item-text { font-size: 1.5rem; font-weight: 600; margin-top: auto; }
.bento-showcase-section .bento-item-stat { background-color: #f3f4f6; text-align: center; justify-content: center; align-items: center;}
.bento-showcase-section .stat-number { font-size: 4.5rem; font-weight: 700; color: var(--color-dark); line-height: 1; }
.bento-showcase-section .stat-label { font-size: 1rem; color: var(--color-secondary); margin-top: 0.5rem; }
.bento-showcase-section .bento-item-testimonial { background-color: #f3f4f6; }
.bento-showcase-section .testimonial-author { display: flex; align-items: center; gap: 0.75rem; margin-bottom: 1rem; }
.bento-showcase-section .author-avatar { width: 40px; height: 40px; border-radius: 50%; object-fit: cover; }
.bento-showcase-section .author-name { font-weight: 600; color: var(--color-dark); }
.bento-showcase-section .author-title { font-size: 0.8rem; color: var(--color-secondary); }
.bento-showcase-section .testimonial-text { font-size: 0.9rem; color: var(--color-dark); }
.bento-showcase-section .anim-delay-1 { transition-delay: 0.1s; }
.bento-showcase-section .anim-delay-2 { transition-delay: 0.2s; }
@media (max-width: 768px) {
    .bento-showcase-section .bento-grid { grid-template-columns: 1fr; }
    .bento-showcase-section .bento-item-large { grid-column: span 1; }
}
