css:
.centered-statement-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.centered-statement-section { padding: 5rem 0; background-color: var(--color-light, #FFFFFF); }
.centered-statement-section h2 {
    max-width: 900px;
    margin: 0 auto;
    text-align: center;
    font-size: 2.5rem;
    line-height: 1.4;
    color: var(--color-dark, #111827);
}
