css:
.feature-cards-header-section { padding: 5rem 0; background-color: var(--color-light, #FFFFFF); }
.feature-cards-header-section .container { width: 90%; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.feature-cards-header {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
    margin-bottom: 4rem;
}
@media (min-width: 768px) {
    .feature-cards-header { flex-direction: row; justify-content: space-between; align-items: flex-end; }
    .feature-cards-header h2 { flex-basis: 40%; }
    .feature-cards-header .header-content { flex-basis: 55%; }
}
.feature-cards-header-section h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    margin: 0;
    color: var(--color-dark, #111827);
}
.feature-cards-header-section .header-content p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 1.5rem;
}
.feature-cards-header-section .btn-primary {
    display: inline-block;
    padding: 0.8rem 2rem;
    background-color: var(--color-dark, #111827);
    color: var(--color-light, #FFFFFF);
    text-decoration: none;
    border-radius: 8px;
    font-weight: 600;
    transition: background-color 0.3s ease;
}
.feature-cards-header-section .btn-primary:hover {
    background-color: #333;
}
.feature-cards-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}
.feature-cards-header-section .feature-card {
    background-color: #f3f4f6;
    border-radius: 1.5rem;
    overflow: hidden;
}
.feature-cards-header-section .card-image-wrapper {
    aspect-ratio: 16 / 10;
}
.feature-cards-header-section .card-image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.feature-cards-header-section .card-content {
    padding: 2rem;
}
.feature-cards-header-section .card-content h3 {
    font-size: 1.5rem;
    margin-bottom: 0.75rem;
    color: var(--color-dark, #111827);
}
.feature-cards-header-section .card-content p {
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
