css:
.founder-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.founder-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.founder-container { max-width: 900px; text-align: center; }
.founder-image { width: 150px; height: 150px; border-radius: 50%; overflow: hidden; margin: 0 auto 2rem; box-shadow: 0 10px 30px rgba(0,0,0,0.1); }
.founder-image img { width: 100%; height: 100%; object-fit: cover; }
.founder-message h2 { font-size: 2.5rem; margin-bottom: 2rem; }
.founder-message blockquote { font-size: 1.5rem; font-style: italic; color: var(--color-dark, #111827); max-width: 700px; margin: 0 auto 2rem; border-left: 4px solid var(--color-primary, #667eea); padding-left: 2rem; text-align: left; }
.signature p { margin: 0; line-height: 1.4; color: var(--color-dark, #111827); }
.signature .role { color: var(--color-secondary, #6B7280); font-size: 0.9rem; }
