css:
.founder-split-section {
    display: grid;
    grid-template-columns: 1fr 1fr;
    min-height: 60vh;
    background-color: var(--color-dark);
}
.founder-split-section .text-side {
    background-color: #000000;
    color: var(--color-light);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 3rem;
}
.founder-split-section .name-block {
    text-align: center;
    width: 100%;
    max-width: 300px;
}
.founder-split-section h2 {
    font-family: serif;
    font-size: 2rem;
    position: relative;
    padding: 1rem 0;
}
.founder-split-section h2::before, .founder-split-section h2::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    height: 1px;
    background: rgba(255, 255, 255, 0.2);
}
.founder-split-section h2::before { top: 0; }
.founder-split-section h2::after { bottom: 0; }
.founder-split-section .name-block p {
    font-family: serif;
    font-size: 1rem;
    margin-top: 1rem;
    border-top: 1px solid rgba(255, 255, 255, 0.2);
    padding-top: 1rem;
}
.founder-split-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
@media (max-width: 768px) {
    .founder-split-section { grid-template-columns: 1fr; }
    .founder-split-section .image-side { order: -1; min-height: 300px; }
}
