css:
.full-bg-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.full-bg-section {
    position: relative;
    padding: 6rem 0;
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    color: var(--color-light, #FFFFFF);
    text-align: center;
}
.full-bg-overlay {
    position: absolute;
    inset: 0;
    background-color: rgba(0,0,0,0.6);
}
.full-bg-section .container {
    position: relative;
    z-index: 1;
}
.full-bg-section h2 { font-size: 3rem; color: var(--color-light, #FFFFFF); margin-bottom: 1rem; }
.full-bg-section p { max-width: 700px; margin: 0 auto; color: #e0e0e0; }
