css:
.about-image-left-stats-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.about-image-left-stats-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
}
.about-image-left-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 992px) {
    .about-image-left-container {
        grid-template-columns: 1fr 1fr;
        gap: 4rem;
    }
}
.about-image-left-wrapper img {
    width: 100%;
    height: 100%;
    max-height: 500px;
    border-radius: 1.5rem;
    object-fit: cover;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.about-image-left-content {
    background-color: #f9fafb;
    padding: 3rem;
    border-radius: 1.5rem;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.about-image-left-content h2 {
    font-size: clamp(2rem, 5vw, 2.75rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 1.5rem;
    line-height: 1.2;
}
.about-image-left-content p {
    color: var(--color-dark, #111827);
    margin-bottom: 1rem;
    opacity: 0.8;
}
.about-image-left-content p.text-secondary {
    color: var(--color-secondary, #6B7280);
    opacity: 0.7;
    font-size: 0.9rem;
}
.about-image-left-stats-section .stats-row {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1.5rem;
    margin-top: 2rem;
}
.about-image-left-stats-section .stat-item h3 {
    font-size: 2.25rem;
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 0.25rem;
}
.about-image-left-stats-section .stat-item p {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
