css:
.numbered-cards-section { padding: 5rem 0; background-color: var(--color-bg, #f9fafb); }
.numbered-cards-section .container { width: 90%; max-width: 1100px; margin: 0 auto; }
.numbered-cards-section .card-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 2rem; }
.numbered-cards-section .card {
    background-color: var(--color-light);
    border: 1px solid var(--color-border, #e5e7eb);
    border-radius: 12px;
    padding: 2rem;
}
.numbered-cards-section .number {
    font-size: 3rem;
    font-weight: 700;
    color: var(--color-dark);
    display: block;
}
.numbered-cards-section hr {
    border: 0;
    height: 1px;
    background-color: var(--color-border, #e5e7eb);
    margin: 1rem 0;
}
.numbered-cards-section h3 {
    font-size: 1.25rem;
    color: var(--color-dark);
}
.numbered-cards-section p {
    color: var(--color-secondary);
    font-size: 0.9rem;
}
