css:
.about-office-image-bottom-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.about-office-image-bottom-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
    text-align: center;
}
.about-office-image-bottom-section .section-header {
    margin-bottom: 2rem;
}
.about-office-image-bottom-section .section-header h2 {
    font-size: 2.5rem;
    color: var(--color-dark, #111827);
    position: relative;
    display: inline-block;
    padding: 0 1rem;
}
.about-office-image-bottom-section .section-header h2::before,
.about-office-image-bottom-section .section-header h2::after {
    content: '';
    position: absolute;
    top: 50%;
    width: 50px;
    height: 1px;
    background-color: #ccc;
}
.about-office-image-bottom-section .section-header h2::before {
    right: 100%;
    margin-right: 1rem;
}
.about-office-image-bottom-section .section-header h2::after {
    left: 100%;
    margin-left: 1rem;
}
.about-office-image-bottom-section .description-box {
    padding: 2rem;
    margin-bottom: 2rem;
}
.about-office-image-bottom-section .description-box p {
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
.about-office-image-bottom-section .cta-wrapper {
    margin-bottom: 3rem;
}
.about-office-image-bottom-section .btn-dark {
    background-color: var(--color-dark, #111827);
    color: var(--color-light, #FFFFFF);
    padding: 0.75rem 2.5rem;
    text-decoration: none;
    font-weight: 600;
    transition: all 0.3s;
}
.about-office-image-bottom-section .btn-dark:hover {
    background-color: #333;
}
.about-office-image-bottom-section .image-wrapper {
    border: 2px solid var(--color-dark, #111827);
    padding: 0.5rem;
}
.about-office-image-bottom-section .image-wrapper img {
    width: 100%;
    height: auto;
    display: block;
}
.about-office-image-bottom-section .anim-delay-1 { transition-delay: 0.1s; }
.about-office-image-bottom-section .anim-delay-2 { transition-delay: 0.2s; }
.about-office-image-bottom-section .anim-delay-3 { transition-delay: 0.3s; }
