css:
.about-office-split-section { padding: 5rem 0; background-color: var(--color-light); }
.about-office-split-section .container { width: 90%; max-width: 1100px; margin: 0 auto; }
.about-office-split-container { display: grid; grid-template-columns: 1fr; gap: 3rem; align-items: center; }
@media (min-width: 768px) { .about-office-split-container { grid-template-columns: 1fr 1fr; } }
.about-office-split-section .about-office-text { padding: 1rem; }
.about-office-split-section .about-office-text h2 { font-size: 2rem; color: #c47d34; border-bottom: 2px solid #c47d34; padding-bottom: 0.5rem; display: inline-block; margin-bottom: 1.5rem; }
.about-office-split-section .about-office-text p { color: var(--color-secondary); }
.about-office-split-section .about-office-image img { width: 100%; height: auto; object-fit: cover; }
