css:
.new-about-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.new-about-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
}
.new-about-section .about-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 1.5rem;
    align-items: start;
}
.new-about-section .grid-item {
    width: 100%;
}
.new-about-section .about-header {
    grid-column: 1 / 2;
    grid-row: 1 / 2;
    align-self: center;
}
.new-about-section .pretitle {
    display: inline-block;
    padding: 0.25rem 0.75rem;
    background-color: #e5e7eb;
    color: var(--color-secondary, #6B7280);
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    margin-bottom: 1rem;
}
.new-about-section .about-header h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    line-height: 1.1;
}
.new-about-section .main-image {
    grid-column: 2 / 3;
    grid-row: 1 / 2;
}
.new-about-section .mission-card {
    grid-column: 3 / 4;
    grid-row: 1 / 2;
}
.new-about-section .stat-card:nth-of-type(4) { /* Use more specific selector if needed */
    grid-column: 1 / 2;
    grid-row: 2 / 3;
}
.new-about-section .stat-card:nth-of-type(5) {
    grid-column: 2 / 3;
    grid-row: 2 / 3;
}
.new-about-section .secondary-image {
    grid-column: 3 / 4;
    grid-row: 2 / 3;
}
.new-about-section .card {
    background-color: #f3f4f6; /* light gray */
    padding: 2rem;
    border-radius: 1rem;
    height: 100%;
}
.new-about-section .card h3 {
    font-size: 1.75rem;
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 1rem;
}
.new-about-section .card h4 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #111827);
    margin-bottom: 0.5rem;
}
.new-about-section .card p {
    color: var(--color-secondary, #6B7280);
    font-size: 0.95rem;
    margin-bottom: 1rem;
}
.new-about-section .card p:last-of-type {
    margin-bottom: 0;
}
.new-about-section .stat-number {
    font-size: 3rem;
    font-weight: 800;
    color: var(--color-dark, #111827);
    line-height: 1;
    margin-bottom: 0.5rem;
    display: block;
}
.new-about-section img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1rem;
}

@media (max-width: 992px) {
    .new-about-section .about-grid {
        grid-template-columns: repeat(2, 1fr);
    }
    .new-about-section .about-header { grid-column: 1 / 3; grid-row: 1; }
    .new-about-section .main-image { grid-column: 1 / 2; grid-row: 2; }
    .new-about-section .mission-card { grid-column: 2 / 3; grid-row: 2; }
    .new-about-section .stat-card:nth-of-type(4) { grid-column: 1 / 2; grid-row: 3; }
    .new-about-section .stat-card:nth-of-type(5) { grid-column: 2 / 3; grid-row: 3; }
    .new-about-section .secondary-image { grid-column: 1 / 3; grid-row: 4; }
}

@media (max-width: 768px) {
    .new-about-section .about-grid {
        grid-template-columns: 1fr;
    }
    .new-about-section .grid-item {
        grid-column: auto !important;
        grid-row: auto !important;
    }
}
.new-about-section .anim-delay-1 { transition-delay: 0.1s; }
.new-about-section .anim-delay-2 { transition-delay: 0.2s; }
.new-about-section .anim-delay-3 { transition-delay: 0.3s; }
.new-about-section .anim-delay-4 { transition-delay: 0.4s; }
