css:
.reading-split-section {
    padding: 3rem 0;
    background-color: var(--color-bg, #f9fafb);
}
.reading-split-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
    background-color: #e0f2fe; /* Light blue background */
    border-radius: 12px;
    padding: 2rem;
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 768px) {
    .reading-split-section .container { grid-template-columns: 1fr 1fr; padding: 3rem; }
}
.reading-split-section h2 {
    font-size: 2rem;
    font-weight: 700;
    color: var(--color-dark);
    margin-bottom: 1.5rem;
}
.reading-split-section p {
    color: var(--color-secondary);
    line-height: 1.7;
}
.reading-split-section .image-content img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 8px;
}
