css:
.about-split-content-image-section { padding: 5rem 0; background-color: var(--color-light, #FFFFFF); }
.about-split-content-image-section .container { width: 90%; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.about-split-content-image-container { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media (min-width: 992px) { .about-split-content-image-container { grid-template-columns: 1fr auto 1fr; gap: 3rem; } }
.about-split-content-image-section .content-col { max-width: 380px; }
.about-split-content-image-section .content-col h2 { font-size: clamp(2.5rem, 5vw, 3.5rem); font-weight: 700; margin-bottom: 1.5rem; color: var(--color-dark, #111827); line-height: 1.2;}
.about-split-content-image-section .content-col h3 { font-size: 1.5rem; font-weight: 600; margin-bottom: 1rem; color: var(--color-dark, #111827);}
.about-split-content-image-section .content-col p { color: var(--color-secondary, #6B7280); margin-bottom: 1rem; }
.about-split-content-image-section .image-col img { border-radius: 1.5rem; max-width: 300px; width: 100%; box-shadow: 0 10px 30px rgba(0,0,0,0.1); display: block; }
