css:
.about-section-split .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.about-section-split { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.about-section-split .about-container { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media (min-width: 768px) { .about-section-split .about-container { grid-template-columns: 1fr 1fr; gap: 4rem; } }
.about-section-split .about-image img { border-radius: 12px; box-shadow: 0 10px 30px rgba(0,0,0,0.1); width: 100%; height: auto; object-fit: cover; }
.about-section-split .about-content h2 { font-size: 2.5rem; margin-bottom: 1rem; color: var(--color-dark, #111827); }
.about-section-split .about-content p { color: var(--color-secondary, #6B7280); margin-bottom: 1.5rem; }
.about-section-split .about-content ul { list-style: none; padding: 0; margin-top: 1.5rem; }
.about-section-split .about-content li { margin-bottom: 0.75rem; color: var(--color-dark, #111827); }
