css:
.about-stats-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.about-stats-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
}
.about-stats-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
}
@media (min-width: 992px) {
    .about-stats-container {
        grid-template-columns: 1fr 1fr;
    }
}
.about-stats-content h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 1.5rem;
    line-height: 1.2;
}
.about-stats-content p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 1rem;
}
.about-stats-section .stats-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1.5rem;
    margin-top: 2.5rem;
}
.about-stats-section .stat-card {
    background-color: #f3f4f6; /* A light gray */
    padding: 1.5rem;
    border-radius: 12px;
}
.about-stats-section .stat-card.highlighted {
    background-color: #d1d5db; /* A darker gray */
}
.about-stats-section .stat-card h3 {
    font-size: clamp(2rem, 4vw, 2.5rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 0.5rem;
}
.about-stats-section .stat-card.highlighted h3 {
    color: var(--color-dark, #111827);
}
.about-stats-section .stat-card p {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
.about-stats-section .stat-card.highlighted p {
    color: var(--color-dark, #111827);
    opacity: 0.9;
}

.about-stats-image-wrapper {
    position: relative;
    height: 100%;
}
.about-stats-image-wrapper::before {
    content: '';
    position: absolute;
    top: -10px;
    left: 0;
    right: 0;
    height: 10px;
    background-image: radial-gradient(var(--color-secondary, #6B7280) 25%, transparent 25%);
    background-size: 10px 10px;
    opacity: 0.2;
}
.about-stats-image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    border-radius: 16px;
}
