css:
.about-stats-gradient-section { padding: 5rem 0; background-color: var(--color-light); }
.about-stats-gradient-section .container { width: 90%; max-width: 1000px; margin: 0 auto; }
.about-stats-gradient-container { display: grid; grid-template-columns: 1fr 1.5fr; gap: 3rem; align-items: center; }
.about-stats-gradient-section .gradient-image {
    width: 100%;
    aspect-ratio: 3/4;
    border-radius: 1rem;
    background: linear-gradient(180deg, #ffecd2 0%, #fcb69f 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    color: rgba(0,0,0,0.5);
    font-size: 0.9rem;
}
.about-stats-gradient-section .stats-content h2 { font-family: serif; font-size: 3rem; color: var(--color-dark); }
.about-stats-gradient-section .stats-content p { color: var(--color-secondary); margin-bottom: 2rem; }
.about-stats-gradient-section .stats-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 1px; background-color: #e5e7eb; border: 1px solid #e5e7eb; }
.about-stats-gradient-section .stat-block { background-color: var(--color-light); padding: 1.5rem; text-align: center; }
.about-stats-gradient-section .stat-value { display: block; font-size: 2.5rem; font-weight: 600; color: var(--color-dark); }
.about-stats-gradient-section .stat-label { font-size: 1rem; color: var(--color-secondary); }
@media (max-width: 768px) {
    .about-stats-gradient-container { grid-template-columns: 1fr; }
}
