css:
.section-about-story-v1 {
  background-color: #dcdcdc;
  padding: 80px 0;
  font-family: 'Helvetica Neue', sans-serif;
}

.section-about-story-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.section-about-story-v1 .story-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
}

@media (max-width: 768px) {
  .section-about-story-v1 .story-grid {
    grid-template-columns: 1fr;
  }
}

.section-about-story-v1 .image-col img {
  width: 100%;
  border-radius: 40px;
  box-shadow: 0 15px 30px rgba(0,0,0,0.1);
}

.section-about-story-v1 .content-col {
  text-align: left;
}

.section-about-story-v1 .story-title {
  font-size: 2.5rem;
  font-weight: 700;
  color: #1a1a1a;
  margin-bottom: 20px;
}

.section-about-story-v1 .story-text {
  color: #555;
  line-height: 1.6;
  margin-bottom: 20px;
}

.section-about-story-v1 .story-list {
  list-style-type: disc;
  padding-left: 20px;
  color: #555;
  margin-bottom: 30px;
}

.section-about-story-v1 .story-list li {
  margin-bottom: 10px;
}

.section-about-story-v1 .btn-outline {
  background: transparent;
  border: 2px solid #555;
  color: #333;
  padding: 10px 30px;
  font-weight: 700;
  text-transform: uppercase;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s;
}

.section-about-story-v1 .btn-outline:hover {
  background: #333;
  color: #fff;
}

.section-about-story-v1 .credit {
  margin-top: 20px;
  font-size: 0.8rem;
  color: #888;
}
