css:
.overlap-section-reversed .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.overlap-section-reversed { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.overlap-container-reversed { position: relative; max-width: 1000px; margin: 0 auto; display: flex; justify-content: flex-end; align-items: center; min-height: 500px; }
.overlap-image-reversed { position: absolute; top: 0; left: 0; width: 60%; height: 100%; border-radius: 12px; overflow: hidden; box-shadow: 0 15px 40px rgba(0,0,0,0.15); }
.overlap-image-reversed img { width: 100%; height: 100%; object-fit: cover; }
.overlap-text-box-reversed { position: relative; z-index: 1; width: 55%; background: var(--color-white, #fff); padding: 3rem; border-radius: 12px; box-shadow: 0 10px 30px rgba(0,0,0,0.1); }
@media (max-width: 768px) {
    .overlap-container-reversed { flex-direction: column; min-height: auto; }
    .overlap-image-reversed { position: relative; width: 100%; height: 300px; margin-bottom: -50px; order: 1; }
    .overlap-text-box-reversed { width: 90%; order: 2; }
}
