css:
.overlap-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.overlap-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.overlap-container { position: relative; max-width: 1000px; margin: 0 auto; display: flex; justify-content: flex-start; align-items: center; min-height: 500px; }
.overlap-image { position: absolute; top: 0; right: 0; width: 60%; height: 100%; border-radius: 12px; overflow: hidden; box-shadow: 0 15px 40px rgba(0,0,0,0.15); }
.overlap-image img { width: 100%; height: 100%; object-fit: cover; }
.overlap-text-box { position: relative; z-index: 1; width: 55%; background: var(--color-white, #fff); padding: 3rem; border-radius: 12px; box-shadow: 0 10px 30px rgba(0,0,0,0.1); }
@media (max-width: 768px) {
    .overlap-container { flex-direction: column; min-height: auto; }
    .overlap-image { position: relative; width: 100%; height: 300px; margin-bottom: -50px; }
    .overlap-text-box { width: 90%; }
}
