css:
.travel-love-section { padding: 5rem 0; background-color: var(--color-light); }
.travel-love-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
    text-align: center;
    padding: 2rem;
}
.travel-love-section .title-wrapper {
    position: relative;
    margin-bottom: 2rem;
}
.travel-love-section .gradient-blur {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 200px;
    height: 100px;
    background: radial-gradient(ellipse at center, #A855F7 0%, rgba(168, 85, 247, 0) 70%);
    filter: blur(40px);
    z-index: 0;
}
.travel-love-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark);
    position: relative;
    z-index: 1;
}
.travel-love-section p {
    color: var(--color-secondary);
    line-height: 1.7;
}
