css:
.values-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.values-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.values-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.values-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 2rem; }
.value-card { text-align: center; padding: 2rem; }
.value-icon { font-size: 3rem; margin-bottom: 1rem; }
.value-card h3 { font-size: 1.5rem; margin-bottom: 0.5rem; color: var(--color-dark, #111827); }
.value-card p { color: var(--color-secondary, #6B7280); }
