css:
.values-section-from-image {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.values-section-from-image .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.values-container-from-image {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .values-container-from-image {
        grid-template-columns: 45% 55%;
        gap: 4rem;
    }
}
.values-section-from-image .values-title {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 1.5rem;
    line-height: 1.2;
}
.values-section-from-image .values-content p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 1rem;
}
.values-section-from-image .values-cards {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}
.values-section-from-image .value-card {
    background-color: #f3f4f6;
    padding: 2rem;
    border-radius: 12px;
}
.values-section-from-image .value-card h3 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #111827);
    margin-bottom: 0.5rem;
}
.values-section-from-image .value-card p {
    font-size: 0.95rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 0;
}
