css:
.why-choose-us-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.why-choose-us-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.why-choose-us-container { display: grid; grid-template-columns: 1fr; gap: 3rem; align-items: center; }
@media (min-width: 768px) { .why-choose-us-container { grid-template-columns: 1fr 1fr; } }
.why-choose-us-content h2 { font-size: 2.5rem; margin-bottom: 1rem; }
.why-choose-us-content p { margin-bottom: 2rem; }
.why-choose-us-list { list-style: none; padding: 0; }
.why-choose-us-list li { font-size: 1.1rem; font-weight: 500; margin-bottom: 1rem; display: flex; align-items: center; }
.why-choose-us-list li span { color: var(--color-primary, #667eea); font-weight: bold; margin-right: 0.75rem; font-size: 1.5rem; }
.why-choose-us-image img { border-radius: 12px; width: 100%; }
