css:
.mission-vision-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.mission-vision-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.mission-vision-container { display: grid; grid-template-columns: 1fr; gap: 2rem; }
@media(min-width: 768px) { .mission-vision-container { grid-template-columns: 1fr 1fr; } }
.mission-vision-section .card { padding: 2.5rem; border-radius: 12px; }
.mission-vision-section .card h3 { font-size: 1.8rem; margin-bottom: 1rem; }
.mission-card { background-color: var(--color-primary, #667eea); color: var(--color-light, #FFFFFF); }
.mission-card h3, .mission-card p { color: var(--color-light, #FFFFFF); }
.vision-card { background-color: #f0f0f0; color: var(--color-dark, #111827); }
