css:
.article-alternating-media-section { padding: 4rem 0; }
.article-alternating-media-section .container.text-content { max-width: 800px; margin: 0 auto; padding: 3rem 1rem; }
.article-alternating-media-section h1 { font-size: 3rem; margin-bottom: 2rem; }
.article-alternating-media-section p { line-height: 1.8; color: var(--color-secondary); margin-bottom: 1.5rem; }
.article-alternating-media-section .full-width-media { width: 100%; height: 60vh; }
.article-alternating-media-section .full-width-media img { width: 100%; height: 100%; object-fit: cover; }
.article-alternating-media-section blockquote { font-size: 1.5rem; font-style: italic; color: var(--color-dark); border-left: 4px solid var(--color-primary); padding-left: 1.5rem; margin: 2.5rem 0; }
