css:
.article-classic-section { font-family: 'Inter', sans-serif; background-color: var(--color-light, #FFFFFF); color: var(--color-dark, #1F2937); }
.article-classic-section .article-header { position: relative; height: 50vh; display: flex; align-items: center; justify-content: center; text-align: center; color: var(--color-light, #FFFFFF); background-size: cover; background-position: center; }
.article-classic-section .header-overlay { position: absolute; inset: 0; background-color: rgba(0,0,0,0.5); z-index: 1; }
.article-classic-section .container { width: 90%; max-width: 800px; margin: 0 auto; position: relative; z-index: 2; }
.article-classic-section .article-header h1 { font-size: clamp(2.5rem, 6vw, 4rem); font-weight: 700; line-height: 1.2; margin: 0; text-shadow: 0 2px 10px rgba(0,0,0,0.3); }
.article-classic-section .meta { margin-top: 1rem; opacity: 0.9; font-style: italic; }
.article-classic-section .article-body { padding: 4rem 0; }
.article-classic-section .article-body p { font-size: 1.1rem; line-height: 1.8; color: var(--color-secondary, #6B7280); margin-bottom: 1.5rem; }
.article-classic-section .article-body h2 { font-size: 2rem; margin: 2.5rem 0 1.5rem; }
.article-classic-section .anim-delay-1 { animation-delay: 0.1s; }
.article-classic-section .anim-delay-2 { animation-delay: 0.2s; }
