css:
.article-dark-section { padding: 4rem 0; background-color: var(--color-dark, #111827); color: var(--color-light); }
.article-dark-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-dark-section h1 { font-size: 3rem; text-align: center; margin-bottom: 2.5rem; color: var(--color-primary); text-shadow: 0 0 15px var(--color-primary); }
.article-dark-section p { font-size: 1.1rem; line-height: 1.8; margin-bottom: 1.5rem; color: var(--color-secondary); }
.article-dark-section img { width: 100%; border-radius: 1rem; margin: 2rem 0; }
.article-dark-section .anim-delay-1 { animation-delay: 0.1s; }
.article-dark-section .anim-delay-2 { animation-delay: 0.2s; }
