css:
.article-feature-left-section { padding: 4rem 0; }
.article-feature-left-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; }
.article-feature-left-section .article-grid { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media(min-width: 768px) { .article-feature-left-section .article-grid { grid-template-columns: 1fr 1fr; gap: 3rem; } }
.article-feature-left-section .image-container img { width: 100%; border-radius: 1rem; aspect-ratio: 4/5; object-fit: cover; }
.article-feature-left-section .text-container h1 { font-size: 2.5rem; margin-bottom: 1.5rem; }
.article-feature-left-section .text-container p { line-height: 1.7; color: var(--color-secondary); margin-bottom: 1rem; }
