css:
.article-title-card-section { position: relative; padding-bottom: 4rem; }
.article-title-card-section .background-image { position: absolute; top: 0; left: 0; right: 0; height: 60vh; background-size: cover; background-position: center; z-index: -1; }
.article-title-card-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-title-card-section .title-card { background: var(--color-light); padding: 2.5rem; border-radius: 1rem; box-shadow: 0 10px 30px rgba(0,0,0,0.1); text-align: center; margin-top: 40vh; }
.article-title-card-section h1 { font-size: 3rem; margin-bottom: 0.5rem; }
.article-title-card-section .title-card p { color: var(--color-secondary); font-style: italic; }
.article-title-card-section .article-body { padding-top: 3rem; }
.article-title-card-section .article-body p { line-height: 1.8; }
