css:
.article-overlap-section { padding: 4rem 0; }
.article-overlap-section .container { max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.article-overlap-section .overlap-grid { display: grid; grid-template-columns: 1fr; align-items: center; }
@media(min-width: 768px) { .article-overlap-section .overlap-grid { grid-template-columns: repeat(12, 1fr); } }
.article-overlap-section .image-container { grid-column: 1 / -1; grid-row: 1; }
@media(min-width: 768px) { .article-overlap-section .image-container { grid-column: 1 / 8; } }
.article-overlap-section .image-container img { width: 100%; border-radius: 1rem; display: block; }
.article-overlap-section .text-container { grid-column: 1 / -1; grid-row: 1; background: var(--color-light); padding: 2rem; border-radius: 1rem; box-shadow: 0 10px 30px rgba(0,0,0,0.1); margin-top: -2rem; }
@media(min-width: 768px) { .article-overlap-section .text-container { grid-column: 7 / 13; margin-top: 0; } }
.article-overlap-section .text-container h2 { font-size: 1.8rem; margin-bottom: 1rem; }
.article-overlap-section .text-container p { line-height: 1.7; color: var(--color-secondary); }
