css:
.article-in-content-gallery-section { padding: 4rem 0; }
.article-in-content-gallery-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-in-content-gallery-section h1 { font-size: 2.5rem; margin-bottom: 2rem; }
.article-in-content-gallery-section p { line-height: 1.7; color: var(--color-secondary); margin-bottom: 1.5rem; }
.article-in-content-gallery-section .gallery-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; margin: 2rem 0; }
.article-in-content-gallery-section .gallery-grid img { width: 100%; aspect-ratio: 1/1; object-fit: cover; border-radius: 0.5rem; }
