css:
.article-interview-section { padding: 4rem 0; }
.article-interview-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-interview-section h1 { font-size: 2.5rem; text-align: center; margin-bottom: 3rem; }
.article-interview-section .qa-pair { margin-bottom: 2.5rem; }
.article-interview-section .question { font-weight: 700; font-size: 1.2rem; color: var(--color-dark); margin-bottom: 0.75rem; }
.article-interview-section .answer { line-height: 1.7; color: var(--color-secondary); margin-left: 1rem; border-left: 2px solid var(--color-primary); padding-left: 1rem; }
