css:
.article-magazine-section { padding: 4rem 0; }
.article-magazine-section .container { max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.article-magazine-section h1 { font-size: 3rem; text-align: center; margin-bottom: 2.5rem; font-family: serif; }
.article-magazine-section .article-body { column-count: 2; column-gap: 2.5rem; }
.article-magazine-section .article-body p { line-height: 1.7; margin-top: 0; margin-bottom: 1.5rem; color: var(--color-secondary); }
@media (max-width: 640px) { .article-magazine-section .article-body { column-count: 1; } }
.article-magazine-section .anim-delay-1 { animation-delay: 0.1s; }
