css:
.article-typography-section { padding: 4rem 0; font-family: serif; }
.article-typography-section .container { max-width: 700px; margin: 0 auto; padding: 0 1rem; }
.article-typography-section h1 { font-size: 3rem; text-align: center; margin-bottom: 2.5rem; line-height: 1.2; }
.article-typography-section p { font-size: 1.1rem; line-height: 1.9; color: var(--color-secondary); margin-bottom: 1.5rem; text-align: justify; }
.article-typography-section p:first-of-type::first-letter { font-size: 4rem; float: left; margin-right: 0.5rem; line-height: 0.8; font-weight: 600; color: var(--color-primary); }
.article-typography-section .anim-delay-1 { animation-delay: 0.1s; }
.article-typography-section .anim-delay-2 { animation-delay: 0.2s; }
