css:
.article-photo-story-section {
    width: 100%;
    overflow-x: hidden; /* Prevents horizontal overflow */
}
.article-photo-story-section .full-width-image {
    width: 100%;
    height: 60vh; /* Reduced height for better proportion */
    max-height: 650px; /* Added max-height for very tall screens */
}
.article-photo-story-section .full-width-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block; /* Removes potential whitespace below the image */
}
.article-photo-story-section .text-block {
    max-width: 700px;
    margin: 0 auto;
    padding: 5rem 1rem; /* Increased padding for more breathing room */
    text-align: center;
}
.article-photo-story-section .text-block h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem); /* Responsive font size */
    font-weight: 700;
    margin-bottom: 1.5rem;
    line-height: 1.2;
}
.article-photo-story-section .text-block p {
    font-size: 1.1rem; /* Slightly larger font for readability */
    line-height: 1.8;
    color: var(--color-secondary);
}
