css:
.article-split-section { padding: 4rem 0; font-family: 'Inter', sans-serif; }
.article-split-section .container { max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.article-split-section .article-grid { display: grid; grid-template-columns: 1fr; gap: 2rem; }
@media (min-width: 768px) { .article-split-section .article-grid { grid-template-columns: 1fr 1fr; gap: 3rem; } }
.article-split-section .image-column { position: sticky; top: 2rem; align-self: start; }
.article-split-section .image-column img { width: 100%; border-radius: 1rem; object-fit: cover; aspect-ratio: 3/4; }
.article-split-section .text-column h1 { font-size: 2.5rem; font-weight: 700; margin-bottom: 2rem; }
.article-split-section .text-column p { line-height: 1.8; margin-bottom: 1.5rem; color: var(--color-secondary); }
.article-split-section .text-column blockquote { font-size: 1.2rem; font-style: italic; border-left: 3px solid var(--color-primary); padding-left: 1.5rem; margin: 2rem 0; }
