css:
.article-technical-section { padding: 4rem 0; background-color: #0d1117; color: #c9d1d9; }
.article-technical-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-technical-section h1 { font-size: 2.5rem; border-bottom: 1px solid #30363d; padding-bottom: 1rem; margin-bottom: 2rem; }
.article-technical-section p { line-height: 1.8; margin-bottom: 1.5rem; }
.article-technical-section pre { background: #161b22; border: 1px solid #30363d; padding: 1.5rem; border-radius: 6px; overflow-x: auto; font-family: 'Roboto Mono', monospace; font-size: 0.9rem; }
.article-technical-section code { all: unset; }

en.code:
.container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1rem;
}

