css:
.article-timeline-section { padding: 4rem 0; }
.article-timeline-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-timeline-section h1 { font-size: 2.5rem; text-align: center; margin-bottom: 2rem; }
.article-timeline-section > .container > p { text-align: center; color: var(--color-secondary); max-width: 600px; margin: 0 auto 3rem; }
.article-timeline-section .timeline { position: relative; padding-left: 2rem; border-left: 3px solid var(--color-primary); }
.article-timeline-section .timeline-item { position: relative; margin-bottom: 2.5rem; }
.article-timeline-section .timeline-item::before { content: ''; position: absolute; left: -2.8rem; top: 5px; width: 18px; height: 18px; border-radius: 50%; background-color: var(--color-light); border: 4px solid var(--color-primary); }
.article-timeline-section .timeline-year { font-weight: 700; color: var(--color-primary); margin-bottom: 0.5rem; }
.article-timeline-section h3 { font-size: 1.5rem; margin-bottom: 0.5rem; }
.article-timeline-section .timeline-item p { line-height: 1.7; color: var(--color-secondary); }
