css:
.article-two-column-section { padding: 4rem 0; }
.article-two-column-section .container { max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.article-two-column-section h1 { font-size: 3rem; text-align: center; margin-bottom: 2.5rem; }
.article-two-column-section .multi-column-content { column-count: 2; column-gap: 2.5rem; }
.article-two-column-section .multi-column-content p { text-align: justify; line-height: 1.7; margin-top: 0; color: var(--color-secondary); }
@media (max-width: 640px) { .article-two-column-section .multi-column-content { column-count: 1; } }
