css:
.article-code-section { padding: 4rem 0; }
.article-code-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-code-section h1 { font-size: 2.5rem; margin-bottom: 2rem; }
.article-code-section p { line-height: 1.7; color: var(--color-secondary); margin: 1.5rem 0; }
.article-code-section pre { background-color: var(--color-dark, #1E293B); color: var(--color-light, #FFFFFF); padding: 1.5rem; border-radius: 0.5rem; overflow-x: auto; font-family: monospace; font-size: 0.9rem; }
.article-code-section code { all: unset; }

en.code:
function createCounter() {
  let count = 0;
  return function() {
    count++;
    return count;
  };
}
const counter = createCounter();
console.log(counter()); // 1

