css:
.article-pullquote-section { padding: 4rem 0; }
.article-pullquote-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.article-pullquote-section h1 { font-size: 3rem; text-align: center; margin-bottom: 2.5rem; }
.article-pullquote-section p { font-size: 1.1rem; line-height: 1.8; margin-bottom: 1.5rem; color: var(--color-secondary); }
.article-pullquote-section .pullquote { font-family: serif; font-size: 2rem; font-weight: 600; color: var(--color-primary); text-align: center; margin: 3rem auto; padding: 0 1rem; max-width: 600px; border: none; }
.article-pullquote-section .pullquote::before, .article-pullquote-section .pullquote::after { content: '"'; font-size: 4rem; opacity: 0.5; line-height: 0; }
.article-pullquote-section .pullquote::before { margin-right: 0.5rem; vertical-align: -0.4em; }
.article-pullquote-section .pullquote::after { margin-left: 0.5rem; vertical-align: -0.6em; }
.article-pullquote-section .anim-delay-1 { animation-delay: 0.1s; }
