css:
.article-sidebar-section { padding: 4rem 0; }
.article-sidebar-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; display: grid; grid-template-columns: 1fr; gap: 3rem; }
@media (min-width: 768px) { .article-sidebar-section .container { grid-template-columns: 2fr 1fr; } }
.article-sidebar-section h1 { font-size: 2.5rem; margin-bottom: 2rem; }
.article-sidebar-section .main-content p { line-height: 1.7; margin-bottom: 1.5rem; color: var(--color-secondary); }
.article-sidebar-section .sidebar h3 { font-size: 1.2rem; font-weight: 600; margin-bottom: 1rem; border-bottom: 1px solid #e5e7eb; padding-bottom: 0.5rem; }
.article-sidebar-section .sidebar ul { list-style: none; padding: 0; }
.article-sidebar-section .sidebar li { margin-bottom: 0.5rem; }
.article-sidebar-section .sidebar a { text-decoration: none; color: var(--color-primary); }
