css:
.section-promo-cta-v1 {
  position: relative;
  height: 500px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.section-promo-cta-v1 .bg-wrapper {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.section-promo-cta-v1 .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-promo-cta-v1 .container {
  position: relative;
  z-index: 2;
  max-width: 1200px;
  width: 100%;
  display: flex;
  justify-content: center;
}

.section-promo-cta-v1 .cta-card {
  background: rgba(220, 220, 220, 0.95);
  padding: 40px;
  max-width: 500px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0,0,0,0.3);
}

.section-promo-cta-v1 .cta-title {
  font-family: 'Helvetica Neue', sans-serif;
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 20px;
  color: #1a1a1a;
}

.section-promo-cta-v1 .cta-text {
  color: #444;
  line-height: 1.6;
  margin-bottom: 20px;
  font-size: 0.95rem;
}

.section-promo-cta-v1 .credit {
  font-size: 0.75rem;
  color: #777;
  margin-bottom: 30px;
  font-style: italic;
}

.section-promo-cta-v1 .btn-cta {
  background: transparent;
  border: 2px solid #333;
  color: #333;
  padding: 12px 35px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 1px;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.section-promo-cta-v1 .btn-cta:hover {
  background: #333;
  color: #fff;
}
