css:
.section-cover-bento-grid-menu {
  background-color: #050505;
  color: #ffffff;
  padding: 80px 0;
  font-family: var(--font-body, sans-serif);
}
.section-cover-bento-grid-menu .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cover-bento-grid-menu .bento-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: 300px 250px;
  gap: 20px;
}
.section-cover-bento-grid-menu .grid-item {
  background: #151515;
  border-radius: 24px;
  overflow: hidden;
  position: relative;
}
.section-cover-bento-grid-menu .main-text {
  grid-column: 1 / 3;
  grid-row: 1 / 2;
  padding: 40px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
}
.section-cover-bento-grid-menu .main-text h1 {
  font-size: 2.5rem;
  line-height: 1.2;
  margin-bottom: 16px;
  color: #fff;
}
.section-cover-bento-grid-menu .main-text p {
  color: #888;
  margin-bottom: 24px;
}
.section-cover-bento-grid-menu .cta-btn {
  background: #ff4500;
  color: white;
  padding: 12px 28px;
  border-radius: 50px;
  text-decoration: none;
  font-weight: 600;
}
.section-cover-bento-grid-menu .img-sq {
  grid-column: 3 / 4;
  grid-row: 1 / 2;
}
.section-cover-bento-grid-menu .img-rect {
  grid-column: 1 / 4;
  grid-row: 2 / 3;
}
.section-cover-bento-grid-menu .stat-box {
  grid-column: 4 / 5;
  grid-row: 1 / 3;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background: #ff4500;
  color: white;
}
.section-cover-bento-grid-menu .big-num {
  font-size: 5rem;
  font-weight: 800;
  line-height: 1;
}
.section-cover-bento-grid-menu .unit {
  font-size: 1.5rem;
  font-weight: 600;
  opacity: 0.9;
}
.section-cover-bento-grid-menu .label {
  margin-top: 10px;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 0.8rem;
}
.section-cover-bento-grid-menu img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s;
}
.section-cover-bento-grid-menu .grid-item:hover img {
  transform: scale(1.1);
}
.section-cover-bento-grid-menu .price-tag {
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(0,0,0,0.7);
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-weight: bold;
}
@media (max-width: 900px) {
  .section-cover-bento-grid-menu .bento-grid {
    grid-template-columns: 1fr;
    grid-template-rows: auto;
  }
  .section-cover-bento-grid-menu .main-text, 
  .section-cover-bento-grid-menu .img-sq, 
  .section-cover-bento-grid-menu .img-rect, 
  .section-cover-bento-grid-menu .stat-box {
    grid-column: auto;
    grid-row: auto;
    min-height: 300px;
  }
}
