css:
.cover-betrieb-bewegung-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-betrieb-bewegung-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-betrieb-bewegung-section .hero-content {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: start;
    margin-bottom: 4rem;
}
@media (min-width: 768px) {
    .cover-betrieb-bewegung-section .hero-content {
        grid-template-columns: 2fr 1fr;
    }
}
.cover-betrieb-bewegung-section .text-content h1 { font-size: clamp(2.5rem, 5vw, 3.5rem); font-weight: 700; color: var(--color-dark, #1F2937); line-height: 1.2; margin-bottom: 1.5rem; }
.cover-betrieb-bewegung-section .text-content p { color: var(--color-secondary, #6B7280); margin-bottom: 2rem; }
.cover-betrieb-bewegung-section .btn-dark { display: inline-block; padding: 0.8rem 2rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border-radius: 0.5rem; font-weight: 600; text-decoration: none; }
.cover-betrieb-bewegung-section .rating-content { background-color: #f3f4f6; padding: 1.5rem; border-radius: 1rem; text-align: center; }
.cover-betrieb-bewegung-section .avatars { display: flex; justify-content: center; margin-bottom: 1rem; }
.cover-betrieb-bewegung-section .avatars img { width: 40px; height: 40px; border-radius: 50%; border: 2px solid var(--color-light, #FFFFFF); margin-left: -10px; }
.cover-betrieb-bewegung-section .stars { color: #f59e0b; margin-bottom: 0.5rem; }
.cover-betrieb-bewegung-section .rating-content p { font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin: 0; }
.cover-betrieb-bewegung-section .stats-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 1.5rem; }
.cover-betrieb-bewegung-section .stat-item, .cover-betrieb-bewegung-section .dark-card { padding: 1.5rem; border-radius: 1rem; }
.cover-betrieb-bewegung-section .stat-item { text-align: center; }
.cover-betrieb-bewegung-section .stat-number { font-size: 3rem; font-weight: 700; color: var(--color-dark, #1F2937); line-height: 1; }
.cover-betrieb-bewegung-section .stat-item p { color: var(--color-secondary, #6B7280); margin-top: 0.5rem; }
.cover-betrieb-bewegung-section .dark-card { background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); }
.cover-betrieb-bewegung-section .dark-card h3 { font-size: 1.25rem; margin-bottom: 0.5rem; color: var(--color-light, #FFFFFF); }
.cover-betrieb-bewegung-section .dark-card p { color: #d1d5db; font-size: 0.9rem; }
