css:
.cover-bild-collage-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-bild-collage-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-bild-collage-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-bild-collage-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-bild-collage-section .content-side {
    max-width: 450px;
}
.cover-bild-collage-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.75rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-bild-collage-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-bild-collage-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-bild-collage-section .collage-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto auto;
    gap: 1rem;
}
.cover-bild-collage-section .collage-item {
    overflow: hidden;
    border-radius: 1.5rem;
}
.cover-bild-collage-section .collage-item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.cover-bild-collage-section .large-item {
    grid-column: 1 / 3;
    grid-row: 1 / 2;
    aspect-ratio: 16 / 9;
}
.cover-bild-collage-section .small-item-1 {
    grid-column: 1 / 2;
    grid-row: 2 / 3;
    aspect-ratio: 1 / 1;
}
.cover-bild-collage-section .small-item-2 {
    grid-column: 2 / 3;
    grid-row: 2 / 3;
    aspect-ratio: 1 / 1;
}
