.section-cover-centered-cta-with-image-d3a4 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 4rem 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 3rem;
}

.section-cover-centered-cta-with-image-d3a4 .content-area {
  max-width: 700px;
}

.section-cover-centered-cta-with-image-d3a4 .cover-title {
  font-size: clamp(2.5rem, 6vw, 4rem);
  margin-bottom: 1rem;
  color: var(--color-dark);
  font-family: var(--font-heading);
}

.section-cover-centered-cta-with-image-d3a4 .cover-subtitle {
  font-size: clamp(1rem, 2.5vw, 1.25rem);
  color: var(--color-secondary);
  margin-bottom: 2rem;
  font-family: var(--font-body);
}

.section-cover-centered-cta-with-image-d3a4 .cta-button-area {
  margin-top: 1rem;
}

.section-cover-centered-cta-with-image-d3a4 .cta-button {
  background-color: var(--color-primary);
  color: var(--color-light);
  padding: 0.8rem 2.5rem;
  border: none;
  border-radius: 5px;
  font-size: 1.1rem;
  cursor: pointer;
  font-family: var(--font-body);
  transition: background-color 0.3s ease;
}

.section-cover-centered-cta-with-image-d3a4 .cta-button:hover {
  background-color: var(--color-secondary);
}

.section-cover-centered-cta-with-image-d3a4 .image-area {
  width: 100%;
  max-width: 450px;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.section-cover-centered-cta-with-image-d3a4 .image-area img {
  width: 100%;
  height: auto;
  display: block;
  object-fit: cover;
}

@media (min-width: 768px) {
  .section-cover-centered-cta-with-image-d3a4 .container {
    flex-direction: row;
    text-align: left;
    gap: 5rem;
  }
  .section-cover-centered-cta-with-image-d3a4 .content-area {
    flex: 1;
  }
  .section-cover-centered-cta-with-image-d3a4 .image-area {
    flex: 1;
    max-width: 50%;
  }
}

js:
(() => {
  const section = document.currentScript.closest('section');
  if (!section) return;

  // Image fetching logic
  const images = section.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    const apiUrl = img.getAttribute('src');
    fetch(apiUrl)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].webformatURL;
          img.alt = data.hits[0].tags || 'AI Generated Image';
        } else {
          img.alt = 'Image not found';
          console.warn('No image found from Pixabay API for:', apiUrl);
        }
      })
      .catch(error => {
        console.error('Error fetching image from Pixabay:', error);
        img.alt = 'Image loading error';
      });
  });
})();
