css:
.section-cover-centered-glass-v4 {
  --bg-color: #e0f2f1;
  --accent-color: #2e3546;
  --card-bg: rgba(255, 255, 255, 0.5);

  background: radial-gradient(circle at 50% 0%, #e3f2fd 0%, #e0f2f1 100%);
  padding: 6rem 2rem;
  text-align: center;
  font-family: 'Inter', sans-serif;
}

.section-cover-centered-glass-v4 .container {
  max-width: 1000px;
  margin: 0 auto;
}

.section-cover-centered-glass-v4 h1 {
  font-size: clamp(2.5rem, 5vw, 4rem);
  color: var(--accent-color);
  margin-bottom: 1.5rem;
  line-height: 1.1;
}

.section-cover-centered-glass-v4 .highlight {
  color: #4f46e5;
  position: relative;
  z-index: 1;
}

.section-cover-centered-glass-v4 .highlight::after {
  content: '';
  position: absolute;
  bottom: 5px;
  left: 0;
  width: 100%;
  height: 10px;
  background: rgba(79, 70, 229, 0.2);
  z-index: -1;
  border-radius: 4px;
}

.section-cover-centered-glass-v4 .lead {
  font-size: 1.25rem;
  color: #556;
  max-width: 600px;
  margin: 0 auto 4rem;
}

.section-cover-centered-glass-v4 .cards-showcase {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
  align-items: center;
}

@media (min-width: 768px) {
  .section-cover-centered-glass-v4 .cards-showcase {
    grid-template-columns: 1fr 1.5fr 1fr;
  }
}

.section-cover-centered-glass-v4 .card-left,
.section-cover-centered-glass-v4 .card-right {
  background: var(--card-bg);
  backdrop-filter: blur(10px);
  padding: 2rem;
  border-radius: 24px;
  border: 1px solid rgba(255,255,255,0.6);
  text-align: left;
  transition: transform 0.3s;
}

.section-cover-centered-glass-v4 .card-left:hover,
.section-cover-centered-glass-v4 .card-right:hover {
  transform: translateY(-5px);
  background: rgba(255,255,255,0.8);
}

.section-cover-centered-glass-v4 .icon-box {
  font-size: 2rem;
  margin-bottom: 1rem;
}

.section-cover-centered-glass-v4 h3 {
  font-size: 1.25rem;
  margin-bottom: 0.5rem;
  color: var(--accent-color);
}

.section-cover-centered-glass-v4 p {
  font-size: 0.9rem;
  color: #667;
  line-height: 1.5;
}

.section-cover-centered-glass-v4 .card-center {
  position: relative;
  border-radius: 32px;
  overflow: hidden;
  box-shadow: 0 20px 50px -10px rgba(0,0,0,0.15);
  aspect-ratio: 4/5;
}

.section-cover-centered-glass-v4 .main-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-cover-centered-glass-v4 .overlay-badge {
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: rgba(46, 53, 70, 0.9);
  color: #fff;
  padding: 0.5rem 1.5rem;
  border-radius: 50px;
  font-size: 0.875rem;
  font-weight: 600;
}

js:
(() => {
  const section = document.querySelector('.section-cover-centered-glass-v4');
  if (!section) return;
  section.querySelectorAll('img').forEach(img => {
     if(img.src.startsWith('https://pixabay.com')) {
        fetch(img.src).then(r=>r.json()).then(d=>{
           if(d.hits && d.hits.length) img.src = d.hits[0].webformatURL;
        });
     }
  });
})();
