css:
.section-cover-centered-gourmet {
  position: relative;
  height: 80vh;
  min-height: 600px;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  color: #fff;
  overflow: hidden;
}
.section-cover-centered-gourmet .bg-img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: 0;
}
.section-cover-centered-gourmet .bg-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to bottom, rgba(0,0,0,0.3), rgba(0,0,0,0.9));
  z-index: 1;
}
.section-cover-centered-gourmet .container {
  position: relative;
  z-index: 2;
  max-width: 800px;
  padding: 20px;
}
.section-cover-centered-gourmet h2 {
  font-family: var(--font-body, sans-serif);
  color: #ff4500;
  text-transform: uppercase;
  letter-spacing: 3px;
  font-size: 1rem;
  margin-bottom: 16px;
  font-weight: 600;
}
.section-cover-centered-gourmet h1 {
  font-family: var(--font-heading, serif);
  font-size: clamp(3rem, 8vw, 6rem);
  line-height: 1.1;
  margin-bottom: 24px;
  text-shadow: 0 4px 12px rgba(0,0,0,0.5);
}
.section-cover-centered-gourmet p {
  font-size: 1.25rem;
  line-height: 1.6;
  color: rgba(255,255,255,0.9);
  margin-bottom: 40px;
}
.section-cover-centered-gourmet .btn-main {
  background: #ff4500;
  border: none;
  padding: 16px 40px;
  color: white;
  font-size: 1.1rem;
  font-weight: bold;
  cursor: pointer;
  border-radius: 50px;
  transition: all 0.3s ease;
}
.section-cover-centered-gourmet .btn-main:hover {
  background: #e63e00;
  transform: scale(1.05);
}
