css:
.section-cover-dark-circular-focus {
  background-color: #111111;
  color: #ffffff;
  padding: 80px 0;
  font-family: var(--font-body, sans-serif);
  overflow: hidden;
}
.section-cover-dark-circular-focus .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cover-dark-circular-focus .hero-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 60px;
  flex-wrap: wrap;
}
.section-cover-dark-circular-focus .text-col {
  flex: 1;
  min-width: 300px;
}
.section-cover-dark-circular-focus .badge {
  display: inline-block;
  font-size: 0.875rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #aaaaaa;
  margin-bottom: 16px;
}
.section-cover-dark-circular-focus h1 {
  font-family: var(--font-heading, serif);
  font-size: clamp(2.5rem, 5vw, 4.5rem);
  line-height: 1.1;
  margin-bottom: 24px;
  font-weight: 700;
}
.section-cover-dark-circular-focus p {
  color: #cccccc;
  font-size: 1.125rem;
  line-height: 1.6;
  margin-bottom: 40px;
  max-width: 480px;
}
.section-cover-dark-circular-focus .cta-group {
  display: flex;
  gap: 20px;
  align-items: center;
  margin-bottom: 48px;
}
.section-cover-dark-circular-focus .btn-primary {
  background-color: #ff4500;
  color: #ffffff;
  padding: 14px 32px;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 600;
  transition: transform 0.2s ease;
}
.section-cover-dark-circular-focus .btn-primary:hover {
  transform: translateY(-2px);
}
.section-cover-dark-circular-focus .btn-text {
  color: #ffffff;
  text-decoration: underline;
}
.section-cover-dark-circular-focus .stats {
  display: flex;
  gap: 40px;
  border-top: 1px solid #333;
  padding-top: 24px;
}
.section-cover-dark-circular-focus .stat-num {
  display: block;
  font-size: 1.5rem;
  font-weight: 700;
  color: #ff4500;
}
.section-cover-dark-circular-focus .stat-label {
  font-size: 0.875rem;
  color: #888;
}
.section-cover-dark-circular-focus .img-col {
  flex: 1;
  min-width: 300px;
  display: flex;
  justify-content: center;
  position: relative;
}
.section-cover-dark-circular-focus .circle-wrapper {
  position: relative;
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
}
.section-cover-dark-circular-focus .hero-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
  box-shadow: 0 20px 60px rgba(0,0,0,0.5);
}
.section-cover-dark-circular-focus .floating-caption {
  position: absolute;
  bottom: 10%;
  left: -20px;
  background: rgba(30,30,30,0.9);
  backdrop-filter: blur(10px);
  padding: 16px 24px;
  border-radius: 12px;
  border-left: 4px solid #ff4500;
}
.section-cover-dark-circular-focus .floating-caption strong {
  display: block;
  color: #fff;
}
.section-cover-dark-circular-focus .floating-caption span {
  font-size: 0.85rem;
  color: #aaa;
}
@media (max-width: 768px) {
  .section-cover-dark-circular-focus .hero-content {
    flex-direction: column-reverse;
    text-align: center;
  }
  .section-cover-dark-circular-focus p,
  .section-cover-dark-circular-focus .cta-group,
  .section-cover-dark-circular-focus .stats {
    justify-content: center;
    margin-left: auto;
    margin-right: auto;
  }
  .section-cover-dark-circular-focus .floating-caption {
    left: 50%;
    transform: translateX(-50%);
    bottom: -20px;
    width: max-content;
  }
}
